import React, { useState } from "react";
import "./index.css";

function App() {
  const [list, setList] = useState([]); //lista de elemente
  const [input, setInput] = useState(""); //inputs

  const addTodo = (todo) => {
    const newTodo = {
      id: Math.random(),
      todo: todo,
    };

    //add the todo to the list
    setList([...list, newTodo]);

    //clear input box
    setInput("");
  };

  const deleteTodo = (id) => {
    //Filter out todos with the id
    const newList = list.filter((todo) => todo.id !== id);

    setList(newList);
  };

  return (
    <div>
      <h1>To do List</h1>
      <input
        className="blankSpace"
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></input>
      <button className="add" onClick={() => addTodo(input)}>
        Add
      </button>
      <ul>
        {list.map((todo) => (
          <li key={todo.id}>
            {todo.todo}
            <button className="close" onClick={() => deleteTodo(todo.id)}>
              &times;
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
