import React, { useState } from "react";

function App() {
  const [list, setList] = useState([]);
  const [input, setInput] = useState("");

  const addTodo = (todo) => {
    const newTodo = {
      id: Math.random(),
      todo: todo,
    };

    //add the todo to the list
    setList([...list, newTodo]);

    //clear input box
    setInput("");
  };

  return (
    <div>
      <h1>To do List</h1>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></input>
      <button onClick={() => addTodo(input)}>Add</button>
      <ul>
        {list.map((todo) => {
          <li key={todo.id}>
            {todo.todo}
            <button>&times;</button>
          </li>;
        })}
      </ul>
    </div>
  );
}

export default App;
